// En tu archivo UpdateHotel.js

import React, { useState, useEffect } from "react";
import axios from "axios";
import swal from "sweetalert";

const endpoint = "http://localhost:8000/api/Hotel/";

const UpdateHotel = ({ hotelId, onUpdateSuccess }) => {
  // Estado para cada campo del formulario
  const [nombreHotel, setNombreHotel] = useState("");
  const [direccion, setDireccion] = useState("");
  const [ciudad, setCiudad] = useState("");
  const [nit, setNit] = useState("");
  const [numeroHabitaciones, setNumeroHabitaciones] = useState("");
  const [idhabitacion, setIdHabitaciones] = useState("");

  // ... puedes agregar más estados para otros campos si los tienes

  // Este useEffect es la clave. Se ejecuta cada vez que 'hotelId' cambia.
  useEffect(() => {
    // Solo hacemos la petición si tenemos un ID válido
    if (hotelId) {
      const getHotelById = async () => {
        try {
          const response = await axios.get(`${endpoint}${hotelId}`);
          // Llenamos el estado del formulario con los datos recibidos
          setNombreHotel(response.data.NombreHotel);
          setDireccion(response.data.Direccion);
          setCiudad(response.data.Ciudad);
          setNit(response.data.Nit);
          setNumeroHabitaciones(response.data.NumeroHabitaciones);
          setIdHabitaciones(response.data.IdHabitaciones);
        } catch (error) {
          console.error("Error fetching hotel data:", error);
        }
      };
      getHotelById();
    }
  }, [hotelId]); // <-- El array de dependencias le dice a React que re-ejecute esto si hotelId cambia

  const handleUpdate = async (e) => {
    e.preventDefault(); // Evita que el formulario recargue la página

    try {
      await axios.put(`${endpoint}${hotelId}`, {
        NombreHotel: nombreHotel,
        Direccion: direccion,
        Ciudad: ciudad,
        Nit: nit,
        NumeroHabitaciones: numeroHabitaciones,
        IdHabitaciones: idhabitacion,
      });

      // Llama a la función del padre para refrescar la lista
      onUpdateSuccess();

      // Cierra el modal (Bootstrap lo hace a través de data-dismiss)
      // y muestra una alerta de éxito
      swal("¡Actualizado!", "El hotel se actualizó correctamente.", "success");

      // Opcionalmente, puedes cerrar el modal con JavaScript si es necesario
      // document.getElementById('close-edit-modal').click();
    } catch (error) {
      console.error("Error updating hotel:", error);
      swal("Error", "No se pudo actualizar el hotel.", "error");
    }
  };

  return (
    <div className="container-fluid container-md">
      <form className="row g-3" onSubmit={handleUpdate}>
        <div className="col-md-4">
          <label>Nombre del Hotel</label>
          <input
            type="text"
            className="form-control"
            value={nombreHotel}
            onChange={(e) => setNombreHotel(e.target.value)}
            required
          />
        </div>
        <div className="col-md-4">
          <label>Dirección</label>
          <input
            type="text"
            className="form-control"
            value={direccion}
            onChange={(e) => setDireccion(e.target.value)}
            required
          />
        </div>
        <div className="col-md-4">
          <label>Ciudad</label>
          <input
            type="text"
            className="form-control"
            value={ciudad}
            onChange={(e) => setCiudad(e.target.value)}
            required
          />
        </div>
        <div className="col-md-4">
          <label>NIT</label>
          <input
            type="text"
            className="form-control"
            value={nit}
            onChange={(e) => setNit(e.target.value)}
            required
          />
        </div>
        <div className="col-md-4">
          <label>Número de Habitaciones</label>
          <input
            type="number"
            className="form-control"
            value={numeroHabitaciones}
            onChange={(e) => setNumeroHabitaciones(e.target.value)}
            required
          />
        </div>
        <div className="col-md-4">
          <label>Tipo de Habitaciones (ID)</label>
          <input
            type="text"
            className="form-control"
            value={idhabitacion}
            onChange={(e) => setIdHabitaciones(e.target.value)}
            required
          />
        </div>
        <div className="modal-footer">
          <button
            type="button"
            className="btn btn-secondary"
            data-dismiss="modal"
          >
            Cancelar
          </button>
          <button type="submit" className="btn btn-primary">
            Guardar Cambios
          </button>
        </div>
      </form>
    </div>
  );
};

export default UpdateHotel;
