import axios from "axios";
import React, {useState,useEffect} from "react";
import { Navigate, useNavigate,useParams } from "react-router-dom";


const endpoint = 'http://localhost:8000/api/Hotel'

const UpdateHotel = ()=>{

    const [NombreHotel,setNombreHotel] = useState('')
    const [Direccion,setDireccion] = useState('')
    const [Ciudad,setCiudad] = useState('')
    const [Nit,setNit] = useState(0)
    const [NumeroHabitaciones,setNumeroHabitaciones] = useState(0)
    const [idhabitacion,setidhabitacion] = useState(0)
    const {id}=useParams()

    const update = async (e) => {
        e.preventDefault()
        await axios.put(`${endpoint}${id}`,{
            NombreHotel: NombreHotel, Direccion:Direccion,Ciudad:Ciudad, Nit:Nit,
            NumeroHabitaciones:NumeroHabitaciones, idhabitacion:idhabitacion 
        })
        Navigate('/')
    }


    useEffect(()=>{
        const getHotelByid = async ()=>{
          const rpta =  axios.get(`${endpoint}${id}`)
            setNombreHotel (rpta.data.NombreHotel)
            setDireccion(rpta.data.Direccion)
            setCiudad(rpta.data.Ciudad)
            setNit((await rpta).data.Nit)
            setNumeroHabitaciones((await rpta).data.NumeroHabitaciones)
            setidhabitacion((await rpta).data.idhabitacion)
        }
    },[])
    return (
         <div> edit</div>
    )

}

export default UpdateHotel
