import React, { useEffect, useState } from "react";
import axios from "axios";
import CreateHotel from "./CreateHotel";
import UpdateHotel from "./UpdateHotel";
import swal from "sweetalert";

// 1. Endpoint estandarizado y en una variable de entorno para producción
const API_ENDPOINT =
  process.env.REACT_APP_API_URL || "http://localhost:8000/api";

const ShowHotel = () => {
  // 2. Estados para manejar carga, errores y datos
  const [hoteles, setHoteles] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // 3. Estado para controlar los modales y el hotel a editar (la "forma React")
  const [isCreateModalOpen, setIsCreateModalOpen] = useState(false);
  const [isEditModalOpen, setIsEditModalOpen] = useState(false);
  const [hotelToEdit, setHotelToEdit] = useState(null);

  useEffect(() => {
    getAllHotel();
  }, []);

  const getAllHotel = async () => {
    setLoading(true);
    setError(null);
    try {
      // Usamos el endpoint estandarizado y la respuesta del backend refactorizado
      const response = await axios.get(`${API_ENDPOINT}/hoteles`);
      setHoteles(response.data.data); // Accedemos a la clave 'data' de nuestra respuesta JSON
    } catch (err) {
      setError(
        "No se pudieron cargar los hoteles. Intente de nuevo más tarde."
      );
      console.error("Error fetching hotels:", err);
      swal({
        text: "Error al cargar los hoteles.",
        icon: "error",
      });
    } finally {
      setLoading(false);
    }
  };

  const deleteHotel = async (id) => {
    swal({
      title: "Eliminar Hotel",
      text: "¿Está seguro que desea eliminar este hotel?",
      icon: "warning",
      buttons: ["No", "Sí"],
      dangerMode: true,
    }).then((willDelete) => {
      if (willDelete) {
        // Usar el endpoint estandarizado
        axios
          .delete(`${API_ENDPOINT}/hotels/${id}`)
          .then(() => {
            swal(
              "¡Eliminado!",
              "El hotel ha sido borrado con éxito.",
              "success"
            );
            getAllHotel(); // Recargar la lista después de borrar
          })
          .catch((err) => {
            console.error("Error deleting hotel:", err);
            swal("Error", "No se pudo borrar el hotel.", "error");
          });
      }
    });
  };

  // 4. Funciones para manejar la apertura y cierre de modales
  const handleOpenEditModal = (hotel) => {
    setHotelToEdit(hotel); // Pasamos el objeto completo, no solo el ID
    setIsEditModalOpen(true);
  };

  const handleCloseModals = () => {
    setIsCreateModalOpen(false);
    setIsEditModalOpen(false);
    setHotelToEdit(null); // Limpiar el estado
  };

  // Función para ser llamada por los componentes hijos tras una operación exitosa
  const handleSuccess = () => {
    handleCloseModals();
    getAllHotel();
  };

  // 5. Lógica de renderizado condicional para la tabla
  const renderTableContent = () => {
    if (loading) {
      return (
        <tr>
          <td colSpan="8" className="text-center">
            Cargando...
          </td>
        </tr>
      );
    }
    if (error) {
      return (
        <tr>
          <td colSpan="8" className="text-center text-danger">
            {error}
          </td>
        </tr>
      );
    }
    if (hoteles.length === 0) {
      return (
        <tr>
          <td colSpan="8" className="text-center">
            No se encontraron hoteles.
          </td>
        </tr>
      );
    }
    return hoteles.map((hotel) => (
      <tr key={hotel.id}>
        <td>{hotel.id}</td>
        <td>{hotel.NombreHotel}</td>
        <td>{hotel.Direccion}</td>
        <td>{hotel.Ciudad}</td>
        <td>{hotel.Nit}</td>
        <td>{hotel.NumeroHabitaciones}</td>
        {/* 6. Mostrar el dato de la relación, no el ID */}
        <td>{hotel.habitacion?.TipoHabitacion ?? "N/A"}</td>
        <td className="text-right py-0 align-middle">
          <div className="btn-group btn-group-sm">
            <button
              type="button"
              className="btn btn-warning"
              onClick={() => handleOpenEditModal(hotel)}
            >
              <i className="fas fa-edit"></i> {/* Icono más apropiado */}
            </button>
            <button
              type="button"
              className="btn btn-danger"
              onClick={() => deleteHotel(hotel.id)}
            >
              <i className="fas fa-trash"></i>
            </button>
          </div>
        </td>
      </tr>
    ));
  };

  return (
    <div className="content-wrapper">
      {/* ... (Cabecera no cambia) ... */}
      <section className="content">
        <div className="container-fluid">
          <div className="row">
            <div className="col-12">
              <div className="card">
                <div className="card-header">
                  <h3 className="card-title">Lista de Hoteles</h3>
                  <div className="card-tools">
                    <button
                      className="btn btn-primary"
                      type="button"
                      onClick={() => setIsCreateModalOpen(true)}
                    >
                      <i className="fas fa-plus"></i> CREAR HOTEL
                    </button>
                  </div>
                </div>
                <div className="card-body table-responsive p-0">
                  <table className="table table-hover text-nowrap">
                    <thead>
                      <tr>
                        <th>#</th>
                        <th>Nombre Hotel</th>
                        <th>Dirección</th>
                        <th>Ciudad</th>
                        <th>Nit</th>
                        <th>N° Habitaciones</th>
                        <th>Tipo Habitación</th>
                        <th>Acciones</th>
                      </tr>
                    </thead>
                    <tbody>{renderTableContent()}</tbody>
                  </table>
                </div>
                {/* ... (Paginación no cambia) ... */}
              </div>
            </div>
          </div>
        </div>
      </section>

      {/* 7. Renderizado condicional de modales controlados por estado de React */}
      {isCreateModalOpen && (
        <CreateHotel
          show={isCreateModalOpen}
          onClose={handleCloseModals}
          onSuccess={handleSuccess}
        />
      )}

      {isEditModalOpen && hotelToEdit && (
        <UpdateHotel
          show={isEditModalOpen}
          onClose={handleCloseModals}
          onSuccess={handleSuccess}
          hotel={hotelToEdit} // Pasamos el objeto completo
        />
      )}
    </div>
  );
};

export default ShowHotel;
