import React, { useEffect, useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

const endpoint = "http://localhost:8000/api";
const ShowHotel = () => {
  const [hoteles, setHoteles] = useState([]);

  useEffect(() => {
    getAllHotel();
  }, []);

  const getAllHotel = async () => {
    const rpta = await axios.get(`${endpoint}/Hoteles`);
    setHoteles(rpta.data);
  };

  const deleteHotel = async (id) => {
    axios.delete(`${endpoint}/Hotel/${id}`);
    getAllHotel();
  };

  return (
    <div className="content">
      <div className="container-fluid">
        <div classname="d-grid gap-2 d-md-flex justify-content-md">
          <link to="/create" classname="btn btn-primary btn-lg" />
          crear
        </div>
        {"{"}hoteles.map((hotel) =&gt; ( )) {"}"}
        <table classname="table table-bordered">
          <thead>
            <tr>
              <th scope="col">#</th>
              <th scope="col">Nombre Hotel</th>
              <th scope="col">Direccion</th>
              <th scope="col">Ciudad</th>
              <th scope="col">Nit</th>
              <th scope="col">Cantidad Habitacionies</th>
              <th scope="col">tipo Habitacionies</th>
              <th scope="col">Acciones</th>
            </tr>
          </thead>
          <tbody>
            <tr key="{hotel.id}">
              <th scope="row">
                {"{"}hotel.id{"}"}
              </th>
              <td>
                {"{"}hotel.NombreHotel{"}"}
              </td>
              <td>
                {"{"}hotel.Direccion{"}"}
              </td>
              <td>
                {"{"}hotel.Ciudad{"}"}
              </td>
              <td>
                {"{"}hotel.Nit{"}"}
              </td>
              <td>
                {"{"}hotel.NumeroHabitaciones{"}"}
              </td>
              <td>
                {"{"}hotel.idhabitacion{"}"}
              </td>
              <td>
                <link
                  to="{`/update/${hotel.id}`}"
                  classname="btn btn-warning"
                />
                Editar
                <button onclick="{()=">
                  deleteHotel(hotel.id){"}"} className='btn btn-danger'&gt;
                  Eliminar{" "}
                </button>
              </td>
            </tr>
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default ShowHotel;
