import axios from "axios";
import React, {useState,useEffect} from "react";
import { Navigate, useNavigate,useParams } from "react-router-dom";


const endpoint = 'http://localhost:8000/api/Hotel'

const UpdateHotel = ()=>{

    const [NombreHotel,setNombreHotel] = useState('')
    const [Direccion,setDireccion] = useState('')
    const [Ciudad,setCiudad] = useState('')
    const [Nit,setNit] = useState(0)
    const [NumeroHabitaciones,setNumeroHabitaciones] = useState(0)
    const [idhabitacion,setidhabitacion] = useState(0)
    const {id}=useParams()

    const update = async (e) => {
        e.preventDefault()
        await axios.put(`${endpoint}${id}`,{
            NombreHotel: NombreHotel, Direccion:Direccion,Ciudad:Ciudad, Nit:Nit,
            NumeroHabitaciones:NumeroHabitaciones, idhabitacion:idhabitacion 
        })
        Navigate('/')
    }


    useEffect(()=>{
        const getHotelByid = async ()=>{
          const rpta =  axios.get(`${endpoint}${id}`)
            setNombreHotel (rpta.data.NombreHotel)
            setDireccion(rpta.data.Direccion)
            setCiudad(rpta.data.Ciudad)
            setNit((await rpta).data.Nit)
            setNumeroHabitaciones((await rpta).data.NumeroHabitaciones)
            setidhabitacion((await rpta).data.idhabitacion)
        }
        getHotelByid()
    },[])
    return (
        <div>
        <h3>Editar Hotel</h3>
        <form onSubmit={update}>
            <div className='mb-3'>
              <label className='form-label'>Nombre Hotel</label>
                <input 
                  value={NombreHotel}
                  onChange={ (e) => setNombreHotel(e.target.value)}
                  type='text'
                  className='form-control'
                />
            </div>

            <div className='mb-3'>
              <label className='form-label'>Direccion</label>
                <input 
                  value={Direccion}
                  onChange={ (e) => setDireccion(e.target.value)}
                  type='text'
                  className='form-control'
                />
            </div>

            <div className='mb-3'>
              <label className='form-label'>Ciudad</label>
                <input 
                  value={Ciudad}
                  onChange={ (e) => setCiudad(e.target.value)}
                  type='text'
                  className='form-control'
                />
            </div>

            <div className='mb-3'>
              <label className='form-label'>Nit</label>
                <input 
                  value={Nit}
                  onChange={ (e) => setNit(e.target.value)}
                  type='number'
                  className='form-control'
                />
            </div>

            <div className='mb-3'>
              <label className='form-label'>Numero de Habitaciones</label>
                <input 
                  value={NumeroHabitaciones}
                  onChange={ (e) => setNumeroHabitaciones(e.target.value)}
                  type='number'
                  className='form-control'
                />
            </div>


            <div className='mb-3'>
              <label className='form-label'>Tipo Habitacion</label>
                <input 
                  value={idhabitacion}
                  onChange={ (e) => setidhabitacion(e.target.value)}
                  type='number'
                  className='form-control'
                />
            </div>

          <button type='submit' className='btn btn-primary'>Guardar</button> 
        </form>
  </div>
    )

}

export default UpdateHotel
