import React, { useEffect, useState } from 'react';
import axios from "axios";
import { Link } from "react-router-dom";





const endpoint = 'http://localhost:8000/api/Hotel';
const endpoint2 = 'http://localhost:8000/api';

const CreateHotel = () => {
  const [NombreHotel, setNombreHotel] = useState('');
  const [Direccion, setDireccion] = useState('');
  const [Ciudad, setCiudad] = useState('');
  const [Nit, setNit] = useState();
  const [NumeroHabitaciones, setNumeroHabitaciones] = useState();
  const [TipoHabitacion, setTipoHabitaciones] = useState('');
  const [TipoAcomodacion, setTipoAcomodacion] = useState('');

  const navigate = useNavigate();

  const [habitacion, setHabitacion] = useState([]);
  const [acomodacion,  setAcomodacion] = useState([]);

  const store = async (e) => {
    e.preventDefault();
    await axios.post(endpoint, {
      NombreHotel: NombreHotel,
      Direccion: Direccion,
      Ciudad: Ciudad,
      Nit: Nit,
      NumeroHabitaciones: NumeroHabitaciones,
      idhabitacion: TipoHabitacion,
      idAcomodacion: TipoAcomodacion,
    });
    navigate('/');
  };

   const selectHabitacion = async (e) => {
    setTipoHabitaciones(e.target.value);
  };

  const selectAcomodacion = async (e) => {
    setTipoAcomodacion(e.target.value);
  };

  useEffect(() => {
    getAllHabitacion();
  }, []);

  useEffect(() => {
    getAllAcomodacion();
  }, []);

  const getAllAcomodacion = async () => {
    const rpta = await axios.get(`${endpoint2}/Acomodacion`);
    setAcomodacion(rpta.data);
  };

  const getAllHabitacion = async () => {
    const rpta = await axios.get(`${endpoint2}/Habitaciones`);
    setHabitacion(rpta.data);
  };


const ShowHotel = () => {
  const [hoteles, setHoteles] = useState([]);

  useEffect(() => {
    getAllHotel();
  }, []);

  const getAllHotel = async () => {
    const rpta = await axios.get(`${endpoint2}/Hoteles`);
    setHoteles(rpta.data);
  };

  const deleteHotel = async (id) => {
    axios.delete(`${endpoint2}/Hotel/${id}`);
    getAllHotel();
  };

  return (
    <section className="content-header">
      <div className="col-12">
        <div class="card">
          <div className="card-header">
            <h3 class="card-title">Hoteles</h3>
          </div>

          <div className="card col-sm-2">
            <Link
              to="/create"
              type="button"
              className="btn btn-block bg-gradient-primary btn-lg "
              
            >
              Crear
            </Link>
          </div>
          <div class="card-body">
            <table class="table table-bordered">
              <thead>
                <tr>
                  <th scope="col">#</th>
                  <th scope="col">Nombre Hotel</th>
                  <th scope="col">Direccion</th>
                  <th scope="col">Ciudad</th>
                  <th scope="col">Nit</th>
                  <th scope="col">Cantidad Habitacionies</th>
                  <th scope="col">tipo Habitacionies</th>
                  <th scope="col">Acciones</th>
                </tr>
              </thead>
              <tbody>
                {hoteles.map((hotel) => (
                  <tr key={hotel.id}>
                    <th scope="row">{hotel.id}</th>
                    <td>{hotel.NombreHotel}</td>
                    <td>{hotel.Direccion}</td>
                    <td>{hotel.Ciudad}</td>
                    <td>{hotel.Nit}</td>
                    <td>{hotel.NumeroHabitaciones}</td>
                    <td>{hotel.idhabitacion}</td>
                    <td>
                      <Link
                        to={`/update/${hotel.id}`}
                        className="btn btn-warning"
                      >
                        Editar
                      </Link>
                      <button
                        onClick={() => deleteHotel(hotel.id)}
                        className="btn btn-danger"
                        data-toggle="modal" data-target="#modal-danger"
                      >
                        
                        Eliminar
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>

          <div class="card-footer clearfix">
            <ul class="pagination pagination-sm m-0 float-right">
              <li class="page-item">
                <a class="page-link" href="#">
                  &laquo;
                </a>
              </li>
              <li class="page-item">
                <a class="page-link" href="#">
                  1
                </a>
              </li>
              <li class="page-item">
                <a class="page-link" href="#">
                  2
                </a>
              </li>
              <li class="page-item">
                <a class="page-link" href="#">
                  3
                </a>
              </li>
              <li class="page-item">
                <a class="page-link" href="#">
                  &raquo;
                </a>
              </li>
            </ul>
          </div>
        </div>
      </div>
   



<div class="modal fade" id="modal-lg">
        <div class="modal-dialog modal-lg">
          <div class="modal-content">
            <div class="modal-header">
              <h4 class="modal-title">Large Modal</h4>
              <button type="button" class="close" data-dismiss="modal" aria-label="Close">
                <span aria-hidden="true">&times;</span>
              </button>
            </div>
            <div class="modal-body">
            <div className='container-fluid container-md'>
  
    
  <h3>Crear Hotel</h3>
  <form className='row g-3' onSubmit={store}>
    <div className='col-md-6'>
      <label for='validationDefault01' className='form-label'>
        Nombre Hotel
      </label>
      <input
        value={NombreHotel}
        onChange={(e) => setNombreHotel(e.target.value)}
        type='text'
        className='form-control'
        id='validationDefault01'
        required
      />
    </div>

    <div className='col-md-6'>
      <label for='validationDefault02' className='form-label'>
        Direccion
      </label>
      <input
        value={Direccion}
        onChange={(e) => setDireccion(e.target.value)}
        type='text'
        className='form-control'
        id='validationDefault02'
        required
      />
    </div>

    <div className='col-md-6'>
      <label for='validationDefault03' className='form-label'>
        Ciudad
      </label>
      <input
        value={Ciudad}
        onChange={(e) => setCiudad(e.target.value)}
        type='text'
        className='form-control'
        id='validationDefault03'
        required
      />
    </div>

    <div className='col-md-6'>
      <label for='validationDefault04' className='form-label'>
        Nit
      </label>
      <input
        value={Nit}
        onChange={(e) => setNit(e.target.value)}
        type='number'
        className='form-control'
        id='validationDefault04'
        required
      />
    </div>

    <div className='col-md-6'>
      <label for='validationDefault05' className='form-label'>
        Numero de Habitaciones
      </label>
      <input
        value={NumeroHabitaciones}
        onChange={(e) => setNumeroHabitaciones(e.target.value)}
        type='number'
        className='form-control'
        id='validationDefault05'
        required
      />
    </div>


    <div className='container-fluid container-md'>
  <h3>Habitaciones</h3>

    <div className='col-md-6'>
      <label for='validationDefault06' className='form-label'>
        Tipo Habitaciones
      </label>
      <select
        id='validationDefault06'
        required
        value={TipoHabitacion}
        onChange={selectHabitacion}
        className='form-select'
        aria-label='Default select example'
      >
        <option selected >
          Seleccione el Tipo de Habitacion
        </option>
        {habitacion.map((habitacion) => (
          <option key={habitacion.id} value={habitacion.id}>
            {habitacion.TipoHabitacion}
          </option>
        ))}
      </select>
    </div>

    <div className='col-md-6'>
      <label for='validationDefault07' className='form-label'>
        Tipo Acomodacion
      </label>
      <select
        id='validationDefault07'
        required
        value={TipoAcomodacion}
        onChange={selectAcomodacion}
        className='form-select'
        aria-label='Default select example'
      >
        <option selected >
          Seleccione el Tipo de Acomodacion
        </option>
        {acomodacion.map((acomodacion) => (
          <option key={acomodacion.id} value={acomodacion.id}>
            {acomodacion.TipoAcomodacion}
          </option>
        ))}
      </select>
    </div>

    </div>

    <button type='submit' className='btn btn-primary'>
      Guardar
    </button>
  </form>
</div>
            </div>
            <div class="modal-footer justify-content-between">
              <button type="button" class="btn btn-default" data-dismiss="modal">Close</button>
              <button type="button" class="btn btn-primary">Save changes</button>
            </div>
          </div>
         
        </div>
    
      </div>
      </section>
  );
};
};
export default ShowHotel
